Record record = this.getMainRecord();
try {
    record.addNew();
    record.getCounterField().setString("1");
    record.setKeyArea(DBConstants.MAIN_KEY_AREA);
    if (record.seek(null))
    {
        record.edit();
        record.getField(TestTable.kTestProperties).setData(null);
        record.set();

        record.seek(null);

        this.startStorm();

        SwingWorker<String,Object> worker = new SwingWorker<String,Object>()
        {

            protected String doInBackground() throws Exception
            {
                synchronized(Thread.currentThread())
                {
                    try {
                        Thread.currentThread().wait(25 * WAIT_MULTIPLIER); // 25 Seconds should be plenty
                    } catch (InterruptedException e) {
                        e.printStackTrace();
                    }
                }
                return null;
            }
            /**
             * Called on the event dispatching thread (not on the worker thread)
             * after the <code>construct</code> method has returned.
             */
            public void done()
            {
                Record record = getMainRecord();
                for (int i = 1; i <=COUNT; i++)
                {
                    if (((PropertiesField)record.getField(TestTable.kTestProperties)).getProperty(Integer.toString(i)) == null)
                        return;
                }

                autoStorm();
            }
        };
        worker.execute();
    }
} catch (DBException e) {
    e.printStackTrace();
}        

return true;

