                    }
                    this.getOwner().getField(UserInfo.kUserGroupID).moveFieldToThis(recUserControl.getField(UserControl.kAnonUserGroupID));
                }
                break;
        }
        return super.doRecordChange(field, iChangeType, bDisplayOption);        // Initialize the record
    }

