            out.println(Utility.startTag(DBParams.COMMAND) + Utility.encodeXML(strReturnCommand) + Utility.endTag(DBParams.COMMAND));
            out.println(Utility.endTag(XMLTags.STATUS_TEXT));
        }
    }
