if (packageName.startsWith("."))
    packageName = packageName.substring(1);

if (programControlTemp != null)
	programControlTemp.free();

if (fullPackage.length() == 0)
    return packageName;
else if (packageName.length() == 0)
    return fullPackage;
return fullPackage + '.' + packageName;

